mapper.select:
/* Menu.selectMenus */ SELECT M.mnu_no, M.pre_mnu_no, M.mnu_name, M.mnu_lv, M.url_adr, M.use_yn, M.dp_yn, M.dp_sequence, M.reg_date, M.reg_no, M.mod_date, M.mod_no FROM MENU M WHERE M.USE_YN = #{useYn}
AND M.DP_YN = #{dpYn}
AND EXISTS ( SELECT 1 FROM MENU_ROLES MR WHERE MR.ROL_NO IN ( SELECT AR.ROL_NO FROM ADM_ROLE AR WHERE AR.ADM_NO = #{admNo}) AND MR.MNU_NO = M.MNU_NO )
ORDER BY M.DP_SEQUENCE, M.PRE_MNU_NO

mapper.update:
/* Menu.updateMenu */
UPDATE MENU SET
	mnu_name = #{mnuName},
	url_adr = #{urlAdr},
	dp_yn = #{dpYn},
	dp_sequence = #{dpSequence},
	mod_date = #{modDate},
	mod_no = #{modNo}
WHERE
	mnu_no = #{mnuNo}
